import React from 'react'
import { useState } from 'react'

import classes from './ImageList.module.css'

export default function ImageList({images, ...props}) {
    const [isImageWatch, setIsImageWatch] = useState(false)
    const [imagePage, setImagePage] = useState(0);
    let clientWidth = document.documentElement.scrollWidth

    return (
        <div className={classes.imagelist_wrapper}>
            {
                images.map((imageUrl, index)=>
                    <img
                        src={imageUrl} alt=""
                        className={classes.imagelist_image} key={index}
                        onClick={() => {
                            setIsImageWatch(true)
                            setImagePage(index)
                        }}
                    />
                )
            }
            <div className={isImageWatch ? classes.imagelist_watch : classes.imagelist_watch_disable} >
                <img
                    src={process.env.PUBLIC_URL + 'close.svg'}
                    className={classes.button_close}
                    onClick={() => setIsImageWatch(false)}
                />
                <img
                    src={process.env.PUBLIC_URL + 'next.svg'}
                    className={imagePage < images.length-1 ? classes.next_button : [classes.next_button, classes.button_disable].join(' ')}
                    onClick={() => imagePage < images.length-1 ? setImagePage(imagePage+1) : null}
                />
                <img
                    src={process.env.PUBLIC_URL + 'next.svg'} alt=""
                    className={imagePage != 0 ? classes.prev_button : [classes.prev_button, classes.button_disable].join(' ')}
                    onClick={() => imagePage != 0 ? setImagePage(imagePage-1) : null}
                />

                <div className={classes.imagelist_visible}>
                    <div className={classes.imagelist_all} style={{transform: `translateX(-${imagePage*clientWidth}px)`}}>
                        {
                            images.map((imageUrl, index) =>
                                <div className={classes.imagelist_all_image_block} key={index}>
                                    <img src={imageUrl} alt="" className={classes.imagelist_all_image}  />
                                </div>
                            )
                        }
                    </div>
                </div>
            </div>
        </div>
    )
}
