import React from 'react';
import classes from './Input.module.css'

const ValidationErrors = ({error}) => (
    <ul className={classes.inputErrors}>
        <li className={classes.inputError}>
            <img src={process.env.PUBLIC_URL + 'input_error.svg'} alt="" className={classes.inputErrorImage} />
            <span>{error.message}</span>
        </li>
    </ul>
)

const Input = React.forwardRef(({validationError, ...props}, ref) => {
    return (
        <label>
            <input
                {...props}
                ref={ref}
                className={classes.input}
            />
            {
                validationError ? <ValidationErrors error={validationError} /> : <></>
            }
        </label>

    );
});

export default Input;