import React, {forwardRef} from "react";
import classes from 'UI/person_input/PersonInput.module.css'

const ValidationError = ({validationError}) => {
    return (
        <div className={classes.validationError}>{validationError}</div>
    )
}

const PersonInput = forwardRef(({validationError, ...props}, ref) => {

    return (
        <label className={classes.label}>
            <input
                {...props}
                className={validationError? [classes.input, classes.error].join(' ') : classes.input}
                type="text"
                ref={ref}
            />
            {
                validationError? <ValidationError validationError={validationError} />: <></>
            }
        </label>
    )
})

export default PersonInput;