import React from 'react';
import classes from './SubmitInput.module.css'

const SubmitInput = (props) => {
    return (
        <div className={classes.submitInputWrapper}>
            <input
                className={classes.submitInput}
                value={props.text}
                {...props}
                type='submit'
            />
        </div>


    );
};

export default SubmitInput;