import React from 'react'

import classes from './UserDate.module.css'

export default function UserDate({userName, date, imageUrl, ...props}) {
    return (
        <div className={classes.userdate_wrapper}>
            <img src={imageUrl} alt="" className={classes.image} />
            <div className={classes.userdate}>
                <div className={classes.user_name}>{userName}</div>
                <div className={classes.user_date}>{date}</div>
            </div>
        </div>
    )
}
