import React from 'react'
import UserDate from 'UI/user_date/UserDate'

import classes from './Comment.module.css'

export default function Comment({userName, createDate, userImageUrl, commentText, ...props}) {
    return (
        <div className={classes.comment_wrapper}>
            <UserDate userName={userName} date={createDate} imageUrl={userImageUrl} />
            <div className={classes.comment_text}>
                {commentText}
            </div>
        </div>
    )
}