import { ADD_ALERT, DELETE_ALERT } from 'modules/alert/store/alertActions'

const defaultAlertState = []

export function alertReducer(state = defaultAlertState, action) {
    switch (action.type) {
        case ADD_ALERT:
            return [
                ...state,
                {
                    alertId: Date.now(),
                    alertType: action.payload.alertType,
                    alertMessage: action.payload.alertMessage
                }

            ]
        case DELETE_ALERT:
            return state.filter((alert, index, array) => array.length !== index + 1)
        default:
            return state
    }
}