import React from 'react';
import {useForm} from "react-hook-form";

import CreatePersonForm from 'modules/feedback-form-with-person/components/create-person-form/CreatePersonForm'
import SubmitInput from 'UI/submit_input/SubmitInput'
import CreateFeedbackForm from 'modules/feedback-form-with-person/components/create-feedback-form/CreateFeedbackForm'

const FeedbackFormWithPerson = () => {
    const {
        register,
        reset,
        formState: {errors},
        handleSubmit
    } = useForm({mode: 'onBlur'})

    const formSubmitHandler = (data) => {
        console.log(data)
    }

    return (
        <form onSubmit={handleSubmit(formSubmitHandler)}>
            <CreatePersonForm errors={errors} register={register} />
            <CreateFeedbackForm errors={errors} register={register}  />
            <SubmitInput />
        </form>
    );
};

export default FeedbackFormWithPerson;