import {ADD_COMMENT,
        LOAD_COMMENT,
        START_LOAD_COMMENT,
        END_LOAD_COMMENT,
        ERROR_LOAD_COMMENT
} from 'modules/feedback_list/store/actionTypes'

const defaultCommentState = [
    {userName: 'Сергей', userDate: '20 июля 2022', commentText: 'Согласен с ва23ми!', userImageUrl: process.env.PUBLIC_URL + 'user.jpg'}
]

const defaultCommentLoadState = {
    loading: false,
    error: null,
    errorMessage: ''
}

export function commentReducer(state=defaultCommentState, action) {
    switch (action.type) {
        case ADD_COMMENT:
            return [
                ...state,
                action.payload
            ]
        case LOAD_COMMENT:
            return [
                ...action.payload
            ]
        default:
            return state
    }
}

export function commentLoadReducer (state = defaultCommentLoadState, action) {
    switch (action.type) {
        case START_LOAD_COMMENT:
            return {
                ...state,
                loading: true
            }
        case END_LOAD_COMMENT:
            return {
                ...state,
                loading: false
            }
        case ERROR_LOAD_COMMENT:
            return {
                ...state,
                error: true,
                errorMessage: action.errorMessage
            }
        default:
            return state
    }
}