import axios from "axios";
import {API_URL} from 'constants'

export async function userRegistration({firstName, lastName, email, password1, password2}) {
    //if (password1 !== password2) return
    const user = await axios.post(`${API_URL}/user/registration`, {
            firstName, lastName, email, password: password1
        }, {
            headers: {
                'Content-Type': 'application/json'
            }
        })
    return user
}