import React, {useState} from 'react';

import classes from 'modules/header/components/auth_toggle/AuthToggle.module.css'
import {LOGIN_TOGGLE, REGISTRATION_TOGGLE} from 'modules/header/constants'

const AuthToggle = ({toggleState, setToggleState}) => {

    return (
        <div className={classes.toggle}>
            <div className={[classes.toggleActive, classes[`toggleState${toggleState}`]].join(' ')}></div>
            <div
                className={classes.toggleName}
                onClick={() => setToggleState(LOGIN_TOGGLE)}
            >Вход</div>
            <div
                className={classes.toggleName}
                onClick={() => setToggleState(REGISTRATION_TOGGLE)}
            >Регистрация</div>
        </div>
    );
};

export default AuthToggle;