import {useState} from 'react'

import classes from "modules/header/components/auth_modal/AuthModal.module.css";
import Input from "UI/input/Input";
import Button from "UI/button/Button";


const RegistrationForm = () => {
    const [registrationFormValues, setRegistrationFormValues] = useState({
        email: '',
        password1: '',
        password2: '',
        firstName: '',
        lastName: ''
    })
    return (
        <div className={classes.togglePage}>
            <Input value={registrationFormValues.firstName} placeholder='Имя' type='text'
                   onChange={(e) => setRegistrationFormValues({...registrationFormValues, firstName: e.target.value})}/>
            <Input value={registrationFormValues.lastName} placeholder='Фамилия' type='text'
                   onChange={(e) => setRegistrationFormValues({...registrationFormValues, lastName: e.target.value})}/>
            <Input value={registrationFormValues.email} placeholder='Email' type='email'
                   onChange={(e) => setRegistrationFormValues({...registrationFormValues, email: e.target.value})}/>
            <Input value={registrationFormValues.password1} placeholder='Пароль' type='password'
                   onChange={(e) => setRegistrationFormValues({...registrationFormValues, password1: e.target.value})}/>
            <Input value={registrationFormValues.password2} placeholder='Повторите пароль' type='password'
                   onChange={(e) => setRegistrationFormValues({...registrationFormValues, password2: e.target.value})}/>
            <Button

                text='Зарегистрироваться'
                wrapperStyle={{marginTop: '2rem', display: 'flex', justifyContent: 'center', alignItems: 'center'}}
            />
        </div>
    )
}

export default RegistrationForm