import {AUTH_MODAL_ON, AUTH_MODAL_OFF} from 'modules/header/store/auth_modal_store/action'

const initialModalState = false

export function authModalReducer(state = initialModalState, action) {
    switch (action.type) {
        case AUTH_MODAL_ON:
            return true
        case AUTH_MODAL_OFF:
            return false
        default:
            return state
    }
}