import {LOGIN_USER, LOGOUT_USER} from 'modules/header/store/user_store/action'

const initialUserState = {
    token: null,
    firstName: null,
    lastName: null,
    avatarUrl: null
}

export function userReducer (state = initialUserState, action) {
    switch (action.type) {
        case LOGIN_USER:
            return {
                ...state,
                token: action.payload.token,
                firstName: action.payload.firstName,
                lastName: action.payload.lastName,
                avatarUrl: action.payload.avatarUrl
            }
        case LOGOUT_USER:
            return {
                ...state,
                token: null,
                firstName: null,
                lastName: null,
                avatarUrl: null
            }
        default:
            return state
    }
}