import {createStore, combineReducers, applyMiddleware, compose} from "redux";
import thunk from 'redux-thunk'

import { commentReducer, commentLoadReducer } from 'modules/feedback_list'
import { alertReducer } from 'modules/alert'
import { userReducer } from 'modules/header'
import { authModalReducer } from 'modules/header'


const ext = window.__REDUX_DEVTOOLS_EXTENSION__;
const devtoolMiddleware =
    ext && process.env.NODE_ENV === 'development' ? ext() : f => f;

const rootReducer = combineReducers({
    comment: commentReducer,
    load: commentLoadReducer,
    alert: alertReducer,
    user: userReducer,
    authModal: authModalReducer,
})

export const store = createStore(rootReducer,
    compose(
        applyMiddleware(thunk),
        devtoolMiddleware
    )

)